import NewsCard from "./NewsCard";
import ".././styles/NewsAnnouncementsPreview.css"
import newsimage1 from ".././assets/news1.jpg";
import newsimage2 from ".././assets/news2.jpg";
import backgroundimage from ".././assets/newsannouncementspreview.png";
import useEmblaCarousel from "embla-carousel-react";
import React, { useEffect } from 'react';
function NewsAnnouncementsPreview() {

    const [emblaRef, emblaApi] = useEmblaCarousel({ loop: false }, )
    useEffect(() => {
        if (emblaApi) {
          console.log(emblaApi.slideNodes()) // Access API
        }
      }, [emblaApi])
    return(
        <div class="container">
            <h2 class="section-title">
                    News and Announcements
                </h2>
            <div class="desktop-news">

                <NewsCard 
                    image={newsimage1}
                    title="lorem ipsum"
                    date="01/01/2024"
                    description="Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua."
                />

                <NewsCard
                    image={newsimage2}
                    title="lorem ipsum"
                    date="01/01/2024"
                    description="Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua."
                />
            </div>
            <div class="news-carousel">
                <div class="embla" ref={emblaRef}>
                    <div class="embla__container">
                        <div class="embla__slide first-item"> 
                            <NewsCard 
                                image={newsimage1}
                                title="lorem ipsum"
                                date="01/01/2024"
                                description="Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua."
                            />
                        </div>
                        <div class="embla__slide"> 
                            <NewsCard
                                image={newsimage2}
                                title="lorem ipsum"
                                date="01/01/2024"
                                description="Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua."
                            />
                        </div>
                        <div class="embla__slide"> 
                            <NewsCard
                                image={newsimage2}
                                title="lorem ipsum"
                                date="01/01/2024"
                                description="Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua."
                            />
                        </div>
                    </div>
                </div>
            </div>
            <a class="view-more">
                view more
            </a>
        </div>
    );
}

export default NewsAnnouncementsPreview;
